import React, { useState } from 'react';
import styles from "./Login.module.css";
import { CiUser, CiLock } from "react-icons/ci";

const Login = ({ setIsAuthenticated }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(''); // Clear any previous error

    try {
      const response = await fetch('http://localhost:8000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        throw new Error('Contraseña o email incorrecto');
      }

      const data = await response.json();
      // Assuming the response contains a token
      localStorage.setItem('token', data.token);
      console.log('Login exitoso:', data);

      setIsAuthenticated(true); // Actualiza el estado de autenticación en App.js

      // Redirect to another page or update the UI as needed
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className={styles.login}>
      <div className={styles.loginContainer}>
        <form onSubmit={handleSubmit}>
          <h1>Login</h1>
          <div className={styles.inputBox}>
            <input
              type="text"
              placeholder="Email"
              required
              maxLength="25"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <CiUser className={styles.icon} />
          </div>

          <div className={styles.inputBox}>
            <input
              type="password"
              placeholder="Password"
              required
              maxLength="25"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <CiLock className={styles.icon} />
          </div>

          {error && <p className={styles.error}>{error}</p>}

          <div className={styles.rememberMe}>
            <label>
              <input type="checkbox" /> Remember Me
            </label>
            <a href="#">Forgot Password</a>
          </div>

          <button type="submit">Login</button>

          <div className={styles.register}>
            <p>Don’t have an account? <a href="/signIn">Register</a></p>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
