import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './Screens/Home';
import Login from './Screens/Login';
import Cursos from './Screens/Cursos';
import CursoDetalle from './Screens/CursoDetalle';
import MisCursos from './Screens/MisCursos';
import SignIn from './Screens/SignIn';
import NavBar from './Components/NavBar';
import { CursoProvider } from './Components/CursoContext';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true);
    }
  }, []);

  return (
    <Router>
      <CursoProvider>
        <div className="App">
          <NavBar isAuthenticated={isAuthenticated} />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
            <Route path="/curso/:id" element={<CursoDetalle />} />
            <Route path="/cursos" element={<Cursos />} />
            <Route path="/miscursos" element={isAuthenticated ? <MisCursos /> : <Login setIsAuthenticated={setIsAuthenticated} />} />
            <Route path="/signin" element={<SignIn />} />
          </Routes>
        </div>
      </CursoProvider>
    </Router>
  );
}

export default App;
