import React, { useState, useContext } from 'react';
import styles from "./Cursos.module.css";
import { useNavigate } from 'react-router-dom';
import { CursoContext } from '../context/CursoContext';

const Cursos = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const { setCursos, setCursoSeleccionado } = useContext(CursoContext);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSearchClick = async () => {
    try {
      const response = await fetch(`http://127.0.0.1:8000/courses/name/${searchTerm}`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      console.log('Data received:', data); // Añadir este log para verificar la estructura de los datos recibidos
      setCursos(data); // Set the course data in context
      setError(null); // Clear any previous errors
    } catch (error) {
      console.error("Error fetching course", error);
      setCursos([]);
      setError("Course not found or there was an error fetching the course.");
    }
  };

  const handleCursoClick = (curso) => {
    setCursoSeleccionado(curso);
    navigate(`/curso/${curso.ID}`);
  };

  return (
    <div className={styles.cursos}>
      <div className={styles.searchContainer}>
        <label className={styles.searchLabel} htmlFor="search">Qué estás buscando?</label>
        <input 
          id="search"
          name="search"
          type="text" 
          placeholder="Buscar cursos..." 
          value={searchTerm}
          onChange={handleSearchChange}
          className={styles.searchInput}
        />
        <button onClick={handleSearchClick} className={styles.searchButton}>Buscar</button>
      </div>

      {error && <p className={styles.error}>{error}</p>}

      {cursoSeleccionado && (
        <div className={styles.cursoCard} onClick={() => handleCursoClick(cursoSeleccionado)}>
          <div className={styles.cursoContent}>
            <h3>{cursoSeleccionado.Name}</h3>
            <p>{cursoSeleccionado.Description}</p>
            <p><strong>Duración:</strong> {cursoSeleccionado.Length}</p>
            <p><strong>Profesor:</strong> {cursoSeleccionado.Teachername}</p>
            <p><strong>Keywords:</strong> {cursoSeleccionado.Keywords}</p>
            <p><strong>Requerimientos:</strong> {cursoSeleccionado.Req}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default Cursos;
