import React, { useEffect, useState } from 'react';
import styles from './MisCursos.module.css';

const MisCursos = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchEnrolledCourses = async () => {
      const token = localStorage.getItem('token');
      const userId = localStorage.getItem('userId'); // Asume que tienes el userId guardado en el localStorage

      if (!token || !userId) {
        setError('Usuario no autenticado');
        setLoading(false);
        return;
      }

      try {
        const response = await fetch(`http://localhost:8000/enrollments/user_id/${userId}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (!response.ok) {
          throw new Error('Error al obtener los cursos');
        }

        const data = await response.json();
        setCourses(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchEnrolledCourses();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div className={styles.error}>{error}</div>;

  return (
    <div className={styles.container}>
      <h1>Mis Cursos</h1>
      {courses.length === 0 ? (
        <p>Aún no se ha inscrito a ningún curso</p>
      ) : (
        <div className={styles.coursesList}>
          {courses.map((course) => (
            <div key={course.CourseID} className={styles.courseCard}>
              <h2>{course.name}</h2>
              <p>{course.description}</p>
              <button className={styles.unenrollButton}>Desinscribirse</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MisCursos;
