import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import styles from "./CursoDetalle.module.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faClock, faCheck, faChalkboardTeacher } from '@fortawesome/free-solid-svg-icons';

const CursoDetalle = () => {
  const { id } = useParams();
  const [curso, setCurso] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCurso = async () => {
      try {
        const response = await fetch(`http://localhost:8000/cursos/${id}`);
        if (!response.ok) {
          throw new Error('Failed to fetch course details');
        }
        const data = await response.json();
        setCurso(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchCurso();
  }, [id]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  if (!curso) {
    return <p>Curso no encontrado</p>;
  }

  return (
    <div className={styles.cursoDetalle}>
      <div className={styles.cursoContent}>
        <h1>{curso.name}</h1>
        <img className={styles.cursoImagen} src={curso.image || 'default_image_path'} alt={curso.Name} />
        <button className={styles.inscripcion}>Inscribirse</button>
      </div>
      <p>{curso.description}</p>
      <p><FontAwesomeIcon icon={faClock} /> Duraci√≥n: {curso.Length}</p>
      <p><FontAwesomeIcon icon={faCheck} /> Requisitos: {curso.Req}</p>
      <p><FontAwesomeIcon icon={faChalkboardTeacher} /> Instructor: {curso.Teachername}</p>
    </div>
  );
}

export default CursoDetalle;
